import React, { Component, Fragment } from 'react';
import Product from './Product';
import axios from "axios";
const config = require('../config.json');

export default class Products extends Component {

  state = {
    newproduct: null,
  //   products: [
  //     {
  //       "name" : " test1",
  //       "genres" : "test1",
  //       "description" : "test1"
  //     },
  //     {
  //       "name" : " test2",
  //       "genres" : "test2",
  //       "description" : "test2"
  //     },
  //   ]
  // }
      isLoading:false,
      products: [],
  }

  fetchProducts = async () => {
    // add call to AWS API Gateway to fetch products here
    // then set them in state
    try {
      const res = await axios.get(`${config.api.invokeUrl}/game`);
      const products = res.data;
      this.setState({ products: products });
      console.log(JSON.stringify(products));
    } catch (err) {
      console.log(`An error has occurred: ${err}`);
    }
  }

  async componentDidMount() {
    const response = await fetch(`https://jmulu03w55.execute-api.us-east-1.amazonaws.com/dev/game`);
    const body = await response.json();
    alert(body);
    this.setState({product:body, isLoading:false})
  }

  render() {
    const isLoading = this.state.isLoading;
    const products = this.state.products;
    return (
      <Fragment>
        <section className="section">
          <div className="container">
            <h1>Energy Products</h1>
            <p className="subtitle is-5">Invest in a clean future with our efficient and cost-effective green energy products:</p>
            <br />
            <div className="columns">
              <div className="column">
                <div className="tile is-ancestor">
                  <div className="tile is-4 is-parent  is-vertical">
                    { 
                      this.state.products && this.state.products.length > 0
                      ? this.state.products.map(product => <Product name={product.name} genres={product.genres} description={product.description} key={product.name} />)
                      : <div className="tile notification is-warning">No products available</div>
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </Fragment>
    )
  }
}
