import React, { Component, Fragment } from 'react';
import Product from './Product';
import axios from "axios";
const config = require('../config.json');

export default class ProductAdmin extends Component {

  state = {
    newgame: { 
      "name": "", 
      "genres": "",
      "description":""
    },
    games: []
  }

  handleAddProduct = async (name, event) => {
    event.preventDefault();
    // add call to AWS API Gateway add product endpoint here
    try {
      const params = {
        "name": name,
        "genres": this.state.newgame.genres,
        "description": this.state.newgame.description
      };
      await axios.post(`${config.api.invokeUrl}/products/${name}`, params);
      this.setState({ games: [...this.state.games, this.state.newgame] });
      this.setState({ newgame: { "name": "", "genres": "" ,"description":""}});
    }catch (err) {
      console.log(`An error has occurred: ${err}`);
    }
  }

  handleUpdateProduct = async (name, genres, description) => {
    // add call to AWS API Gateway update product endpoint here
    try {
      const params = {
        "name": name,
        "genres": genres,
        "description": description
      };
      await axios.patch(`${config.api.invokeUrl}/products/${name}`, params);
      const gameToUpdate = [...this.state.names].find(game => game.name === name);
      const updatedGames = [...this.state.games].filter(game => game.name !== name);
      gameToUpdate.productname = name;
      updatedGames.push(gameToUpdate);
      this.setState({products: updatedGames});
    }catch (err) {
      console.log(`Error updating product: ${err}`);
    }
  }

  handleDeleteProduct = async (name, event) => {
    event.preventDefault();
    // add call to AWS API Gateway delete product endpoint here
    try {
      await axios.delete(`${config.api.invokeUrl}/products/${name}`);
      const updatedGames = [...this.state.games].filter(game => game.name !== name);
      this.setState({games: updatedGames});
    }catch (err) {
      console.log(`Unable to delete product: ${err}`);
    }
  }

  fetchProducts = async () => {
    // add call to AWS API Gateway to fetch products here
    // then set them in state
    try {
      const res = await axios.get(`${config.api.invokeUrl}/products`);
      const games = res.data;
      this.setState({ games: games });
    } catch (err) {
      console.log(`An error has occurred: ${err}`);
    }
  }

  onAddGameNameChange = event => this.setState({ newgame: { ...this.state.newgame, "name": event.target.value } });
  onAddGameGenresChange = event => this.setState({ newgame: { ...this.state.newgame, "genres": event.target.value } });
  onAddGameDescChange = event => this.setState({ newgame: { ...this.state.newgame, "description": event.target.value } });

  componentDidMount = () => {
    this.fetchProducts();
  }

  render() {
    return (
      <Fragment>
        <section className="section">
          <div className="container">
            <h1>Product Admin</h1>
            <p className="subtitle is-5">Add and remove products using the form below:</p>
            <br />
            <div className="columns">
              <div className="column is-one-third">
                <form onSubmit={event => this.handleAddProduct(this.state.newgame.name, event)}>
                  <div className="field has-addons">

                    {/* name */}
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter name"
                        value={this.state.newgame.name}
                        onChange={this.onAddGameNameChange}
                      />
                    </div>
                    {/* genres */}
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter genres"
                        value={this.state.newgame.genres}
                        onChange={this.onAddGameGenresChange}
                      />
                    </div>

                    {/* description */}
                    <div className="control">
                      <input 
                        className="input is-medium"
                        type="text" 
                        placeholder="Enter id"
                        value={this.state.newgame.description}
                        onChange={this.onAddGameDescChange}
                      />
                    </div>
                    <div className="control">
                      <button type="submit" className="button is-primary is-medium">
                        Add product
                      </button>
                    </div>
                  </div>
                </form>
              </div>
              <div className="column is-two-thirds">
                <div className="tile is-ancestor">
                  <div className="tile is-4 is-parent  is-vertical">
                    { 
                      this.state.games.map((game, index) => 
                        <Product 
                          isAdmin={true}
                          handleUpdateProduct={this.handleUpdateProduct}
                          handleDeleteProduct={this.handleDeleteProduct} 
                          name={game.name} 
                          genres={game.genres}
                          description={game.description}
                          key={game.name}
                        />)
                    }
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </Fragment>
    )
  }
}
