import React, { Component, Fragment } from 'react';
import Product from './Product';


const config = require('../config.json');

export default class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: [],
        isLoaded: false
    }

}
componentDidMount() {

  fetch('https://jmulu03w55.execute-api.us-east-1.amazonaws.com/dev')
      .then(res => res.json())
      .then(json => {
          this.setState({
              items: json,
              isLoaded: true, 
          })
      }).catch((err) => {
          console.log(err);
      });

}
